import org.meveo.model.customEntities.PROXY;
import org.meveo.model.customEntities.PROXY_GROUP;
import org.meveo.model.customEntities.PROXY_PROVIDER;

public class Buyproxies extends Script {
	
    private static final Logger log = LoggerFactory.getLogger(Buyproxies.class);
    private CrossStorageApi crossStorageApi = getCDIBean(CrossStorageApi.class);
	private RepositoryService repositoryService = getCDIBean(RepositoryService.class);
    private Repository defaultRepo = repositoryService.findDefaultRepository();

	@Override
	public void execute(Map<String, Object> parameters) throws BusinessException {
		log.info("calling Buyproxies");
		CustomEntityInstance cei = (CustomEntityInstance)parameters.get("CONTEXT_ENTITY");
		String pid=cei.getCfValues().getCfValue("code").getStringValue();
		String proxyGroupUuid = cei.getUuid();
		PROXY_GROUP proxyGroup = null;
		try {
			proxyGroup=crossStorageApi.find(defaultRepo, proxyGroupUuid, PROXY_GROUP.class);;
		} catch (EntityDoesNotExistsException e){
			log.error("Cant load proxy group");
			e.printStackTrace();
			return;
		}
		proxyGroup.setUuid(proxyGroupUuid);
        PROXY_PROVIDER proxyProvider = (PROXY_PROVIDER) parameters.get("proxy_provider");
		String tocken = proxyProvider.getToken();
		ResteasyClientBuilder builder= new ResteasyClientBuilder();
        Client client = builder.build();
        WebTarget target = client.target("http://api.buyproxies.org/?a=showProxies&key="+tocken+"&pid="+pid);
		Response response= target.request().get();
		String value = response.readEntity(String.class);
		log.info("response  :"+value);
		log.debug("response status : {}",response.getStatus());
		String login=null,password=null;
      
      	if (response.getStatus() < 300){
			try (BufferedReader br = new BufferedReader(new StringReader(value))) {
				// CSV file delimiter
				String DELIMITER = ":";
				// read the file line by line
				String line;
				while ((line = br.readLine()) != null) {
					// convert line into columns
					String[] columns = line.split(DELIMITER);
					PROXY proxy = new PROXY();
                    proxy.setCreationDate(Instant.now());
                    proxy.setLastUpdate(Instant.now());
            		proxy.setUuid(columns[0]);
					proxy.setGroup(proxyGroup);
					proxy.setIp(columns[0]);
					proxy.setPort(Long.parseLong(columns[1]));
					login=columns[2];
					password=columns[3];
					proxy.setProvider(proxyProvider);
            		log.info("proxy: {} provider:{}",proxy.getUuid());
            		try {
            			crossStorageApi.createOrUpdate(defaultRepo,proxy);
            		} catch(Exception ex){
              			log.error("error creating proxy {} :{}",proxy.getUuid(),ex.getMessage());
            		}
				}
			
			} catch (IOException ex) {
				ex.printStackTrace();
			}
			if(login!=null){
				proxyGroup.setUsername(login);
				proxyGroup.setPassword(password);
				try {
					crossStorageApi.createOrUpdate(defaultRepo,proxyGroup);
				} catch(Exception ex){
					  log.error("error updating proxygroup {} :{}",proxyGroup.getUuid(),ex.getMessage());
				}
			}
            
          
      }
      response.close();
	}
	
}

