package com.webdrone.proxy.deployment;

import java.util.ArrayList;
import java.util.List;
import java.util.Map;


import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;
import org.meveo.model.customEntities.CustomEntityInstance;
import org.meveo.model.persistence.CEIUtils;
import org.meveo.model.customEntities.ApplicationInstance;
import org.meveo.model.customEntities.PROXY_DEPLOYMNT;
import org.meveo.model.customEntities.PROXY_GROUP;
import org.meveo.model.customEntities.PROXY_PROVIDER;
import org.meveo.model.customEntities.PROXY;
import org.meveo.service.storage.RepositoryService;
import org.meveo.model.storage.Repository;
import org.meveo.api.persistence.CrossStorageApi;
