public class WebdroneApiV1 extends Script {
    private static final Logger log = LoggerFactory.getLogger(WebdroneApiV1.class);
    private CrossStorageApi crossStorageApi = getCDIBean(CrossStorageApi.class);
	private RepositoryService repositoryService = getCDIBean(RepositoryService.class);
    private Repository defaultRepo = repositoryService.findDefaultRepository();

    private String getAsJsonDomaineArray(List<String> l){
        String result  = "[";
        if(l!=null){
            String sep="";
            for(String domain:l){
                int type=0;//website, i.e. subdomain, like www.google.com that match www.google.com only
                if(domain.startsWith("*")){
                    if(domain.endsWith("*")){
                        type=2;// pattern like google that match google.com, maps.google.com, google.fr, ...
                        domain = domain.substring(1, domain.length()-1);
                    } else {
                        type=1; // domain like google.com that match google.com, www.google.com,
                        domain = domain.substring(1, domain.length());
                    }
                }
                result+=sep+"{\"pattern\":\""+domain+"\",\"type\":"+type+"}";
                sep=",";
            }
        }
        result+=" ]";
        return result;
    }

    private String getAsJsonArray(List<String> l){
        String result  = "[";
        if(l!=null){
            String sep="";
            for(String repo:l){
                result+=sep+"\""+repo+"\"";
                sep=",";
            }
        }
        result+=" ]";
        return result;
    }

    
    private void setValuesFromJson(JsonObject proxyDepObj,PROXY_DEPLOYMNT proxyDeployment){
        proxyDeployment.setAvailable(Boolean.valueOf(proxyDepObj.get("available").getAsString()));
        proxyDeployment.setActive(Boolean.valueOf(proxyDepObj.get("active").getAsString()));
        if(proxyDepObj.get("lastGeoLocTestDate")!=null && !proxyDepObj.get("lastGeoLocTestDate").isJsonNull()){
            long lastGeoLocTestDate = proxyDepObj.get("lastGeoLocTestDate").getAsLong();
            proxyDeployment.setLastGeoLocTestDate(Instant.ofEpochMilli(lastGeoLocTestDate));
        }
        if(proxyDepObj.get("lastFailedDate")!=null && !proxyDepObj.get("lastFailedDate").isJsonNull()){
            long lastFailedDate = proxyDepObj.get("lastFailedDate").getAsLong();
            proxyDeployment.setLastFailedDate(Instant.ofEpochMilli(lastFailedDate));
        }
        if(proxyDepObj.get("lastSuccesDate")!=null && !proxyDepObj.get("lastSuccesDate").isJsonNull()){
            long lastSuccesDate = proxyDepObj.get("lastSuccesDate").getAsLong();
            proxyDeployment.setLastSuccesDate(Instant.ofEpochMilli(lastSuccesDate));
        }
        if(proxyDepObj.get("repositoryExclus")!=null && !proxyDepObj.get("repositoryExclus").isJsonNull()){
            List<String> repositoryExclus = new ArrayList<>();
            JsonArray array = proxyDepObj.get("repositoryExclus").getAsJsonArray();
            for(JsonElement element:array){
                repositoryExclus.add(element.getAsString());
            }
            proxyDeployment.setExcludedRepos(repositoryExclus);
        }
        if(proxyDepObj.get("repositoryInclus")!=null && !proxyDepObj.get("repositoryInclus").isJsonNull()){
            List<String> repositoryInclus = new ArrayList<>();
            JsonArray array = proxyDepObj.get("repositoryInclus").getAsJsonArray();
            for(JsonElement element:array){
                repositoryInclus.add(element.getAsString());
            }
            proxyDeployment.setIncludedRepos(repositoryInclus);
        }
        
        if(proxyDepObj.get("domainesExclus")!=null && !proxyDepObj.get("domainesExclus").isJsonNull()){
            List<String> domainesExclus = new ArrayList<>();
            JsonArray array = proxyDepObj.get("domainesExclus").getAsJsonArray();
            for(JsonElement element:array){
                String pattern = element.getAsJsonObject().get("pattern").getAsString();
                int type = element.getAsJsonObject().get("type").getAsInt();
                if(type>0){
                    pattern="*"+pattern;
                    if(type>1){
                        pattern+="*";
                    }
                }
                domainesExclus.add(pattern);
            }
            proxyDeployment.setExcludedDomains(domainesExclus);
        }
        if(proxyDepObj.get("domainesInclus")!=null && !proxyDepObj.get("domainesInclus").isJsonNull()){
            List<String> domainesInclus = new ArrayList<>();
            JsonArray array = proxyDepObj.get("domainesInclus").getAsJsonArray();
            for(JsonElement element:array){
                String pattern = element.getAsJsonObject().get("pattern").getAsString();
                int type = element.getAsJsonObject().get("type").getAsInt();
                if(type>0){
                    pattern="*"+pattern;
                    if(type>1){
                        pattern+="*";
                    }
                }
                domainesInclus.add(pattern);
            }
            proxyDeployment.setIncludedDomains(domainesInclus);
        }
    }

    private String getJsonProxyDeployment(PROXY_DEPLOYMNT p){
        String result  = "  {"
            +"\"_id\": \""+p.getUuid()+"\","
            +"\"ip\": \""+p.getProxy().getIp()+"\","
            +"\"port\": "+p.getProxy().getPort()+","
            //+"\"continent\": null,"
            //+"\"country\": null,"
            +"\"isoCountryCode\": \""+p.getProxy().getGroup().getLocation()+"\","
            //+"\"region\": null,"
            //+"\"department\": null,"
            +"\"login\": \""+p.getLogin()+"\","
            +"\"password\": \""+p.getPassword()+"\","
            +"\"webZone\": \""+p.getWebZone()+"\","
            +"\"available\": "+(Boolean.TRUE.equals(p.getAvailable()))+","
            +"\"active\": "+(Boolean.TRUE.equals(p.getActive()))+","
            +"\"lastModifiedDate\": "+System.currentTimeMillis()+",";
        if(p.getProxy().getProvider()!=null && p.getProxy().getProvider().getName()!=null){
            result += "\"provider\": \""+p.getProxy().getProvider().getName().toUpperCase()+"\",";
        }
        if(p.getLastGeoLocTestDate()!=null){
            result += "\"lastGeoLocTestDate\": "+p.getLastGeoLocTestDate().toEpochMilli()+",";
        }
        if(p.getLastFailedDate()!=null){
            result +="\"lastFailedDate\": "+p.getLastFailedDate().toEpochMilli()+",";
        }
        result +="\"locationKO\": "+(Boolean.TRUE.equals(p.getLocationKo()))+",";
        if(p.getLastSuccesDate()!=null){
            result +="\"lastSuccesDate\": "+p.getLastSuccesDate().toEpochMilli()+",";
        }
        result += "\"repositoryInclus\": "+getAsJsonArray(p.getIncludedRepos())+","
            +"\"repositoryExclus\": "+getAsJsonArray(p.getExcludedRepos())+","
            +"\"domainesInclus\": "+getAsJsonDomaineArray(p.getIncludedDomains())+","
            +"\"domainesExclus\": "+getAsJsonDomaineArray(p.getExcludedDomains())
        +"}";
        return result;
    }

    private List<String> getAsStringList(JsonElement node){
        List<String> result=null;
        if(node != null && node.isJsonArray()){
            JsonArray array = node.getAsJsonArray();
            result = new ArrayList<>();
            for(JsonElement item : array){
                result.add(item.getAsString());
            }
        }
        return result;
    }

    private String getApiBaseUrl(String domain,Long port){
        String result = domain;
        if(!domain.startsWith("http")){
            result="http://"+domain;
        }
        if(port>0){
            result+=":"+port;
        }
        return result;
    }

    private void fillAndGetDetail(PROXY_DEPLOYMNT proxyDeployment)  throws Exception {
        ApplicationInstance appInstance = crossStorageApi.find(defaultRepo, proxyDeployment.getAppInstance().getUuid(), ApplicationInstance.class);
        String proxyUuid = proxyDeployment.getProxy().getUuid();
        PROXY proxy = crossStorageApi.find(defaultRepo, proxyUuid, PROXY.class);
        String proxyGroupUuid = proxy.getGroup().getUuid();
        PROXY_GROUP proxyGroup = crossStorageApi.find(defaultRepo, proxyGroupUuid, PROXY_GROUP.class);
        proxyGroup.setDeployedAppInstance(appInstance);
        proxy.setGroup(proxyGroup);
        proxyDeployment.setAppInstance(appInstance);
        proxyDeployment.setProxy(proxy);
        getDetail(proxyDeployment);
    }

    private void getDetail(PROXY_DEPLOYMNT proxyDeployment) throws Exception {
        ResteasyClientBuilder builder= new ResteasyClientBuilder();
        Client client = builder.build();
        String endpointUrl = getApiBaseUrl(proxyDeployment.getAppInstance().getDomain(),proxyDeployment.getAppInstance().getPort())+"/API/ipproxy/get/detail/";
        String id = proxyDeployment.getProxy().getIp()+":"+ proxyDeployment.getProxy().getPort();
        log.info("endpointUrl: {} , id: {}",endpointUrl,id);
        WebTarget target = client.target(endpointUrl);
        Form form = new Form();
        form.param("id", id);
        Entity<Form> entity = Entity.form(form);
        Response response = target.request(MediaType.APPLICATION_JSON).post(entity);
        if (response.getStatus() < 300){
            String value = response.readEntity(String.class);
            log.info("response : {}",value);
            JsonObject proxyObj = new JsonParser().parse(value).getAsJsonObject();
            setValuesFromJson(proxyObj,proxyDeployment);
            try{
                crossStorageApi.createOrUpdate(defaultRepo,proxyDeployment);   
            } catch (Exception e){
                e.printStackTrace();
                log.error("cannot persist proxyDeployment");
                throw new Exception("cannot persist proxyDeployment :"+e.getMessage());
            }
        } else {
            log.error("response status : {}",response.getStatus());
            throw new Exception("response status :"+response.getStatus());
        }
    }

    private void deployProxy(PROXY_DEPLOYMNT proxyDeployment, ApplicationInstance appInstance, boolean isCreation,Client client)  throws Exception{
        String proxyJson = getJsonProxyDeployment(proxyDeployment);
        String endpointUrl = getApiBaseUrl(appInstance.getDomain(),appInstance.getPort())+"/API/ipproxy/";
        WebTarget target = client.target(endpointUrl+(isCreation?"create":"update"));
        Form form = new Form();
        form.param("context", appInstance.getJsonContext());
        form.param("proxy", proxyJson);
        log.info("send context:{}, proxy: {}",appInstance.getJsonContext(),proxyJson);
        Entity<Form> entity = Entity.form(form);
        Response response = target.request(MediaType.APPLICATION_JSON).post(entity);
        String value = response.readEntity(String.class);
        log.info("response : {}",value);
        crossStorageApi.createOrUpdate(defaultRepo,proxyDeployment);
    }

    private void deployProxy(PROXY proxy, Client client) throws Exception {
        CrossStorageRequest<PROXY_DEPLOYMNT> csRequest = crossStorageApi.find(defaultRepo,PROXY_DEPLOYMNT.class);
        String proxyDeploymentUuid = proxy.getIp()+":"+proxy.getPort();
        csRequest.by("uuid",proxyDeploymentUuid);
        List<PROXY_DEPLOYMNT> proxyDeployments = csRequest.getResults();
        PROXY_DEPLOYMNT proxyDeployment = null;
        PROXY_GROUP proxyGroup = proxy.getGroup();
        ApplicationInstance appInstance = proxyGroup.getDeployedAppInstance();
        boolean isCreation=true;
        if(proxyDeployments.size()>0){
            proxyDeployment = proxyDeployments.get(0);
            if(!appInstance.getUuid().equals(proxyDeployment.getAppInstance().getUuid())){
                String error ="Proxy "+proxy.getUuid()+" already deployed on another appInstance : "+proxyDeployment.getAppInstance().getDomain();
                log.error(error);
                throw new Exception(error);
            }
            isCreation =false;
        } else {
            proxyDeployment = new PROXY_DEPLOYMNT();
            proxyDeployment.setUuid(proxyDeploymentUuid);
            proxyDeployment.setAppInstance(appInstance);
            proxyDeployment.setActive(true);
            proxyDeployment.setAvailable(true);
            proxyDeployment.setExcludedDomains(List.of());
            proxyDeployment.setExcludedRepos(List.of());
            proxyDeployment.setIncludedRepos(List.of());
            proxyDeployment.setWebZone("WEB");
        }
        proxyDeployment.setProxy(proxy);
        proxyDeployment.setLogin(proxyGroup.getUsername());
        proxyDeployment.setPassword(proxyGroup.getPassword());
        deployProxy(proxyDeployment,appInstance,isCreation,client);
    }

	@Override
	public void execute(Map<String, Object> parameters) throws BusinessException {
		log.info("calling WebdroneApiV1");
        String actionCode = (String)parameters.get("CONTEXT_ACTION");
        String output=" action:"+actionCode;
		if(parameters.containsKey("CONTEXT_ENTITY")){
			log.info("action called on a specific custom entity instance");
			CustomEntityInstance cei = (CustomEntityInstance)parameters.get("CONTEXT_ENTITY");
			Map<String,Object> cftMap=cei.getCfValuesAsValues();
            if("getDetail".equals(actionCode)){//apply yo PROXY_DEPLOYMNT
                    log.info("get Detail");
                    try {
                        PROXY_DEPLOYMNT proxyDeployment = crossStorageApi.find(defaultRepo, cei.getUuid(), PROXY_DEPLOYMNT.class);
                        fillAndGetDetail(proxyDeployment);
                    } catch(Exception ex){
                        ex.printStackTrace();
                        output+=" failed :"+ex.getMessage();
                    }
            } else if("sendToApp".equals(actionCode)){//apply yo PROXY_DEPLOYMNT
                log.info("sendToApp");
                try {
                    PROXY_DEPLOYMNT proxyDeployment = crossStorageApi.find(defaultRepo, cei.getUuid(), PROXY_DEPLOYMNT.class);
                    String appInstanceUuid = ((CustomEntityInstance)cftMap.get("appInstance")).getUuid();
                    ApplicationInstance appInstance = crossStorageApi.find(defaultRepo, appInstanceUuid, ApplicationInstance.class);
                    ResteasyClientBuilder builder= new ResteasyClientBuilder();
                    Client client = builder.build();
                    deployProxy(proxyDeployment,appInstance,false,client);
                } catch(Exception ex){
                    ex.printStackTrace();
                    output+=" failed :"+ex.getMessage();
                }
            } else if("getDetails".equals(actionCode)){//apply to PROXY_GROUP
                log.info("get Details");
                String proxyGroupUuid = cei.getUuid();
                String appInstanceUuid = ((CustomEntityInstance)cftMap.get("deployedAppInstance")).getUuid();
                CrossStorageRequest<PROXY> csRequest = crossStorageApi.find(defaultRepo,PROXY.class);
                csRequest.by("group",proxyGroupUuid);
                List<PROXY> proxies = csRequest.getResults();
                int count = proxies.size();
                int success= 0;
                log.info("synchronize {} proxy, proxyGroup:{}",count,cftMap);
                for(PROXY proxy:proxies){
                    try {
                        CrossStorageRequest<PROXY_DEPLOYMNT> csRequest2 = crossStorageApi.find(defaultRepo,PROXY_DEPLOYMNT.class);
                        csRequest2.by("proxy",proxy.getUuid());
                        List<PROXY_DEPLOYMNT> proxyDeployments = csRequest2.getResults();
                        int count2 = proxyDeployments.size();
                        if(count2==1){
                            PROXY_DEPLOYMNT proxyDeployment = proxyDeployments.get(0);
                            try {
                                fillAndGetDetail(proxyDeployment);
                                success++;
                            } catch(Exception ex){
                                ex.printStackTrace();
                                output+=" failed :"+ex.getMessage();
                            }
                        } else {
                            if(count2 == 0){
                                output+=" proxy :"+proxy.getUuid()+" is not deployed. ";
                            } else {
                                output+=" proxy :"+proxy.getUuid()+" is deployed more than once. ";
                            }
                        }
                    } catch (Exception e){
                        e.printStackTrace();
                        log.error("cannot synchronize proxy "+proxy.getUuid()+" to appInstance ");
                    }
                }
                output+=" deployed "+success+" proxies / "+count;
            }
            else if("listCount".equals(actionCode)){// apply to ApplicationInstance
              log.info("listCount ");
              String domain = (String) cftMap.get("domain");
              Long port = (Long) cftMap.get("port");
              ResteasyClientBuilder builder= new ResteasyClientBuilder();
              Client client = builder.build();
              if(!domain.startsWith("http")){
                  domain="http://"+domain;
              }
              if(port>0){
                  domain+=":"+port;
              }
              log.info("domain="+domain);
              WebTarget target = client.target(domain+"/API/ipproxy/list/context/filters/count/");
              Response response= target.request().build("post").invoke();
              String value = response.readEntity(String.class);
              log.info("result="+value);
              if (response.getStatus() < 300){
                JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray("data");
                //log.debug("rootArray: {}",rootArray);
                /*if(rootObject.get("servers")!=JsonNull.INSTANCE){
                  Object servers = rootObject.get("servers");
                  log.info("received servers:{}",servers);
                }*/
                for(JsonElement element:rootArray){
                  JsonObject proxyDeploymentObj = element.getAsJsonObject();
                  PROXY_DEPLOYMNT proxyDeployment = new PROXY_DEPLOYMNT();
                  proxyDeployment.setUuid(proxyDeploymentObj.get("_id").getAsString());
                  String ip = proxyDeploymentObj.get("ip").getAsString();
                  PROXY proxy = null;
                  try{
                        proxy = crossStorageApi.find(defaultRepo, ip, PROXY.class);
                        log.info("found proxy: uuid="+proxy.getUuid());
                  } catch (EntityDoesNotExistsException e){}
                  proxyDeployment.setProxy(proxy);
                  ApplicationInstance appInstance = new ApplicationInstance();
                  appInstance.setUuid(cei.getUuid());
                  proxyDeployment.setAppInstance(appInstance);
                  setValuesFromJson(proxyDeploymentObj,proxyDeployment);
                  log.info("proxyDeployment: {} appInstance:{}",proxyDeployment.getUuid(),proxyDeployment.getAppInstance().getUuid());
                  try {
                      crossStorageApi.createOrUpdate(defaultRepo,proxyDeployment);
                  } catch(Exception ex){
                    log.error("error creating proxyDeployment {} :{}",proxyDeployment.getUuid(),ex.getMessage());
                  }
                }
            }
            } else if("DeployToApp".equals(actionCode)){
                String appInstanceUuid = ((CustomEntityInstance)cftMap.get("deployedAppInstance")).getUuid();
                String providerUuid = ((CustomEntityInstance)cftMap.get("provider")).getUuid();
                log.info("DeployToApp:{}, proxy provider : {}",appInstanceUuid,providerUuid);
                
                if(appInstanceUuid!=null && appInstanceUuid.length()>0){}
                    ApplicationInstance appInstance =null;
                    try {
                        appInstance = crossStorageApi.find(defaultRepo, appInstanceUuid, ApplicationInstance.class);
                    } catch (EntityDoesNotExistsException e){
                        e.printStackTrace();
                    }
                    PROXY_PROVIDER provider = null;
                    if(providerUuid!=null){
                        try {
                            provider = crossStorageApi.find(defaultRepo, providerUuid, PROXY_PROVIDER.class);
                            log.info("load provider, name={}",provider.getName());
                        } catch (EntityDoesNotExistsException e){
                            e.printStackTrace();
                        }
                    }
                    if(appInstance!=null){
                        String proxyGroupUuid = cei.getUuid();
                        CrossStorageRequest<PROXY> csRequest = crossStorageApi.find(defaultRepo,PROXY.class);
                        csRequest.by("group",proxyGroupUuid);
                        List<PROXY> proxies = csRequest.getResults();
                        int count = proxies.size();
                        int success= 0;
                        log.info("deploy {} proxy, proxyGroup:{}",count,cftMap);
                        ResteasyClientBuilder builder= new ResteasyClientBuilder();
                        Client client = builder.build();
                        for(PROXY proxy:proxies){
                            try {
                                proxy.getGroup().setUsername(""+cftMap.get("username"));
                                proxy.getGroup().setPassword(""+cftMap.get("password"));
                                proxy.getGroup().setLocation(""+cftMap.get("location"));
                                proxy.getGroup().setDeployedAppInstance(appInstance);
                                proxy.getGroup().setProvider(provider);
                                deployProxy(proxy,client);
                                success++;
                            } catch (Exception e){
                                e.printStackTrace();
                                log.error("cannot deploy proxy "+proxy.getUuid()+" to appInstance ");
                            }
                        }
                        output+=" deployed "+success+" proxies / "+count;
                    } else {
                        output+=" cannot deploy proxy group if it is not associated to an existing AppInstance";
                    }
                } else {
                    output+=" cannot deploy proxy group if it is not associated to an AppInstance or a proxy provider";
                }
            } else {
                output+="action called on a list of custom entity instances";
			   //action called on a list of custom entity instances
               if("getDetail".equals(actionCode)){
                   log.info("getDetails : {}",parameters);
               }
		}
		log.info("output:"+output);
        parameters.put("RESULT_GUI_MESSAGE",output);
	}

}

package com.webdrone.proxy;

import org.meveo.service.script.Script;
