public class ListScalewayServersScript extends Script {
	
    private static final Logger log = LoggerFactory.getLogger(ListScalewayServersScript.class);
    private CrossStorageApi crossStorageApi = getCDIBean(CrossStorageApi.class);
	private RepositoryService repositoryService = getCDIBean(RepositoryService.class);
    private Repository defaultRepo = repositoryService.findDefaultRepository();
  
    private SERVICE_PROVIDER getProvider(String code){
      return crossStorageApi.find(defaultRepo, SERVICE_PROVIDER.class)
    		.by("CODE", code)
    		.getResult();
    }
      
    private CREDENTIAL getCredential(String domain){
      List<CREDENTIAL> matchigCredentials = crossStorageApi.find(defaultRepo, CREDENTIAL.class)
                .by("DOMAIN", domain)
				.getResults();
      if(matchigCredentials.size()>0){
        return matchigCredentials.get(0);
      } else {
        return null;
      }
    }
  
	@Override
	public void execute(Map<String, Object> parameters) throws BusinessException {
	  log.info("calling ListScalewayServersScript");
      SERVICE_PROVIDER scaleway=null;
      try {
        scaleway = getProvider("SCALEWAY");
      } catch(Exception e){
        throw new BusinessException(e);
      }
      String domain="api.scaleway.com";
      CREDENTIAL credential  = getCredential(domain);
      if(credential==null){
        throw new BusinessException("No credential found for "+domain);
      } else {
        log.info("using credential {} with username {}",credential.getUuid(),credential.getUSERNAME());
      }
      ResteasyClientBuilder builder= new ResteasyClientBuilder();
      Client client = builder.build();
      WebTarget target = client.target("https://"+domain+"/instance/v1/zones/fr-par-1/servers");
      
      Response response= target.request().header(credential.getHEADER_KEY(), credential.getHEADER_VALUE()).get();
      String value = response.readEntity(String.class);
      log.info("response  :"+value);
      log.debug("response status : {}",response.getStatus());
      
      if (response.getStatus() < 300){
          JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray("servers");
          //log.debug("rootArray: {}",rootArray);
          /*if(rootObject.get("servers")!=JsonNull.INSTANCE){
            Object servers = rootObject.get("servers");
            log.info("received servers:{}",servers);
          }*/
          for(JsonElement element:rootArray){
            JsonObject serverObj = element.getAsJsonObject();
            SERVER server = new SERVER();
            server.setUuid(serverObj.get("id").getAsString());
            server.setINSTANCE_NAME(serverObj.get("name").getAsString());
            server.setPROVIDER(scaleway);
            log.info("server: {} provider:{}",server.getUuid(),server.getPROVIDER().getUuid());
            try {
            	crossStorageApi.createOrUpdate(defaultRepo,server);
            } catch(Exception ex){
              log.error("error creating server {} :{}",server.getUuid(),ex.getMessage());
            }
          }
      }
      response.close();
	}
	
}

