public class UnitProxyLocalizationTestScript extends Script {

    private static final Logger log = LoggerFactory.getLogger(UnitProxyLocalizationTestScript.class);

    private CrossStorageApi crossStorageApi = getCDIBean(CrossStorageApi.class);
    private CrossStorageService crossStorageService = getCDIBean(CrossStorageService.class);
    private RepositoryService repositoryService = getCDIBean(RepositoryService.class);
    private Repository defaultRepo = repositoryService.findDefaultRepository();


	@Override
	public void execute(Map<String, Object> parameters) throws BusinessException {
		log.info("calling ProxyActionHandler");
        String actionCode = (String)parameters.get("CONTEXT_ACTION");
        String output="";
		if(parameters.containsKey("CONTEXT_ENTITY")){
			//action called on a specific custom entity instance
			CustomEntityInstance cei = (CustomEntityInstance)parameters.get("CONTEXT_ENTITY");
			Map<String,Object> cftMap=cei.getCfValuesAsValues();
            String testIp = (String)cftMap.get("testIP");
            int testPort = -1;
            PROXY proxy = crossStorageApi.find(defaultRepo, PROXY.class)
                .by("uuid", ((CustomEntityInstance)cftMap.get("proxy")).getUuid()).fetch("group").getResult();
            log.info("testIp={}, proxy={}",testIp,proxy);
            String queryEL = (String) cftMap.get("queryEL");
            String result = executeProxyRequest(testIp,proxy,queryEL);
            cei.getCfValues().setValue("testResult",result);
            try {
            	crossStorageService.createOrUpdate(defaultRepo,cei);
            } catch(Exception e){
              e.printStackTrace();
            }
            parameters.put("RESULT_GUI_MESSAGE", "test result:"+result);
        }
    }

    public static String executeProxyRequest(String ip,PROXY proxy, String queryEL){
        String result="";
        String query = queryEL.replaceAll("#\\{proxy.ip\\}", ip);
        log.info("query : {}",query);
        HTTPCall call = new HTTPCall();
        call.setMethod(HTTPMethod.GET);
        call.setUrl(query);
        if(proxy!=null){
            Proxy ceProxy = new Proxy();
            ceProxy.setIp(proxy.getIp());
            ceProxy.setPort(""+proxy.getPort());
            ceProxy.setLogin(proxy.getGroup().getUsername());
            ceProxy.setPassword(proxy.getGroup().getPassword());
            call.setProxy(ceProxy);
        }
        HTTPCallAnalyzer analyzer = new HTTPCallAnalyzer();
        ExecutionContext executionContext =new ExecutionContext();
        call.setResponseVariableName("response");
        call.setAcceptedResponseCodes(List.of(200,201,202,203,204,205,206));
        try {
            NodeContext nodeContext = analyzer.execute(call, executionContext, new NodeContext());
            result= ""+executionContext.get("response");
        } catch (Exception ce){
            result = "error "+ce.getMessage();
        }
        return result;
    }

}

