        }
        return geoLiteISPReader;
    }

    protected void checkBddBuilDate(Reader reader, String checkSumFile) {
        Date geoLiteBddBuildDate = reader.getMetadata().getBuildDate();
        if ((System.currentTimeMillis() - geoLiteBddBuildDate.getTime()) > (31l /* <= nb jours*/ *24*3600*1000)) {
            LOGGER.error("Geolite BDD is not up to date :" + geoLiteBddBuildDate + ". Program will remove checksumfile to force download");
            LOGGER.info("Geolite BDD should be updated each month");
            try {
                FileUtils.forceDelete(new File(rootPath + checkSumFile));
            } catch (FileNotFoundException e) {
                //ignored
            } catch (Exception e) {
                LOGGER.error(e.getMessage(),e);
            }
        }
    }

