    }

    public static void invalidate(String databaseFile) {
        if (GEOLITE_DATABASE_FILE.equals(databaseFile))
            invalidateCountryReader();
        else if (GEOLITE_CITY_DATABASE_FILE.equals(databaseFile))
            invalidateCityReader();
        else
            invalidateISPReader();
    }
