package org.meveo.script.openstack;

import java.util.Map;
import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import javax.ws.rs.client.*;
import javax.ws.rs.core.*;
import com.google.gson.*;
import java.util.List;
import java.util.ArrayList;
import org.meveo.model.customEntities.Credential;

public class OpenstackAPI extends Script {

    private static final Logger log = LoggerFactory.getLogger(ListOVHServersScript.class);

    private String computeBaseAPI = "https://compute.gra11.cloud.ovh.net/v2.1/";

    private String networkBaseAPI = "https://network.compute.gra11.cloud.ovh.net/v2.0/";

    private String imageBaseAPI = "https://image.compute.gra11.cloud.ovh.net/v2/";

    private String identityBaseAPI = "https://auth.cloud.ovh.net/";

    /**
     * Do all of compute api call
     * @param url the path of the call
     * @param token the token currently used by the api
     * @param jsonBody of the request. Can be null if no body is needed
     * @param methodType GET, POST, DELETE, PUT
     * @param objReturn to return is the json response
     * @return the list of json object
     * @throws if the methodType used is not supported
     */
    public List<JsonObject> computeAPI(String url, Credential token, String jsonBody, String methodType, String objReturn) throws BusinessException {
        List<JsonObject> res = new ArrayList<>();
        Client client = ClientBuilder.newClient();
        if (methodType.equalsIgnoreCase("get")) {
            WebTarget target = client.target(this.computeBaseAPI + url);
            Response response = target.request().header("X-Auth-Token", token.getToken()).get();
            String value = response.readEntity(String.class);
            if (response.getStatus() < 300) {
              	String isList = "\"" + objReturn + "s\": [";
                if (value.contains(isList)) {
                    JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray(objReturn);
                    for (JsonElement element : rootArray) {
                        JsonObject JObject = element.getAsJsonObject();
                        res.add(JObject);
                    }
                } else {
                    JsonObject JObject = new JsonParser().parse(value).getAsJsonObject();
                    JObject = JObject.get(objReturn).getAsJsonObject();
                  	res.add(JObject);
                }
            }
            response.close();
        } else if (methodType.equalsIgnoreCase("post")) {
            WebTarget target = client.target(this.computeBaseAPI + url);
            Response response = target.request().header("X-Auth-Token", token.getToken()).post(Entity.json(jsonBody));
            String value = response.readEntity(String.class);
            if (response.getStatus() < 300) {
                JsonParser parserServer = new JsonParser();
                JsonElement jsonServer = parserServer.parse(value);
                JsonObject serverObj = jsonServer.getAsJsonObject();
                serverObj = serverObj.get(objReturn).getAsJsonObject();
                res.add(serverObj);
            }
            response.close();
        } else if (methodType.equalsIgnoreCase("delete")) {
            WebTarget target = client.target(this.computeBaseAPI + url);
            Response response = target.request().header("X-Auth-Token", token.getToken()).delete();
            String value = response.readEntity(String.class);
            response.close();
        } else if (methodType.equalsIgnoreCase("put")) {
        } else {
            throw new BusinessException("Cannot found " + methodType + " in method type request. Available methods : get, post, delete, put");
        }
        client.close();
        return res;
    }

    /**
     * Do all of network api call
     * @param url the path of the call
     * @param token the token currently used by the api
     * @param jsonBody of the request. Can be null if no body is needed
     * @param methodType GET, POST, DELETE, PUT
     * @param objReturn to return is the json response
     * @return the list of json object
     * @throws if the methodType used is not supported
     */
    public List<JsonObject> networkAPI(String url, Credential token, String jsonBody, String methodType, String objReturn) {
        List<JsonObject> res = new ArrayList<>();
        Client client = ClientBuilder.newClient();
        WebTarget target = client.target(this.networkBaseAPI + url);
        Response response = target.request().header("X-Auth-Token", token.getToken()).get();
        String value = response.readEntity(String.class);
        if (response.getStatus() < 300) {
            JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray(objReturn);
            for (JsonElement element : rootArray) {
                JsonObject JObject = element.getAsJsonObject();
                res.add(JObject);
            }
        }
        response.close();
        client.close();
        return res;
    }

    /**
     * Do all of image api call
     * @param url the path of the call
     * @param token the token currently used by the api
     * @param jsonBody of the request. Can be null if no body is needed
     * @param methodType GET, POST, DELETE, PUT
     * @param objReturn to return is the json response
     * @return the list of json object
     * @throws if the methodType used is not supported
     */
    public List<JsonObject> imageAPI(String url, Credential token, String jsonBody, String methodType, String objReturn) {
        List<JsonObject> res = new ArrayList<>();
        Client client = ClientBuilder.newClient();
        WebTarget target = client.target(this.imageBaseAPI + url);
        Response response = target.request().header("X-Auth-Token", token.getToken()).get();
        String value = response.readEntity(String.class);
        if (response.getStatus() < 300) {
            JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray(objReturn);
            for (JsonElement element : rootArray) {
                JsonObject JObject = element.getAsJsonObject();
                res.add(JObject);
            }
        }
        response.close();
        client.close();
        return res;
    }

    /**
     * Do all of identity api call
     * @param url the path of the call
     * @param token the token currently used by the api
     * @param jsonBody of the request. Can be null if no body is needed
     * @param methodType GET, POST, DELETE, PUT
     * @param objReturn to return is the json response
     * @return the list of json object
     * @throws if the methodType used is not supported
     */
    public List<JsonObject> IdentityAPI(String url, Credential token, String jsonBody, String methodType, String objReturn) {
        List<JsonObject> res = new ArrayList<>();
        Client client = ClientBuilder.newClient();
        WebTarget target = client.target(this.identityBaseAPI + url);
        Response response = target.request().header("X-Auth-Token", token.getToken()).get();
        String value = response.readEntity(String.class);
        if (response.getStatus() < 300) {
            JsonArray rootArray = new JsonParser().parse(value).getAsJsonObject().getAsJsonArray(objReturn);
            for (JsonElement element : rootArray) {
                JsonObject JObject = element.getAsJsonObject();
                res.add(JObject);
            }
        }
        response.close();
        client.close();
        return res;
    }

    @Override
    public void execute(Map<String, Object> parameters) throws BusinessException {
        super.execute(parameters);
    }
}

package org.meveo.script.openstack;

import java.util.Map;
import org.meveo.service.script.Script;
import org.meveo.admin.exception.BusinessException;
import org.meveo.api.exception.EntityDoesNotExistsException;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.meveo.model.customEntities.ServiceProvider;
import org.meveo.model.customEntities.Credential;
import java.util.ArrayList;
import java.util.HashMap;
import java.time.OffsetDateTime;
import org.meveo.model.persistence.JacksonUtil;
import javax.ws.rs.client.*;
import javax.ws.rs.core.*;
import org.meveo.api.persistence.CrossStorageApi;
import org.meveo.model.storage.Repository;
import org.meveo.service.storage.RepositoryService;
import java.util.List;
import org.meveo.model.persistence.CEIUtils;
import org.meveo.model.customEntities.CustomEntityInstance;
import org.meveo.service.custom.CustomEntityTemplateService;
import org.meveo.service.crm.impl.CustomFieldTemplateService;
import org.meveo.model.customEntities.CustomEntityTemplate;
import org.meveo.model.crm.CustomFieldTemplate;
import org.meveo.security.PasswordUtils;
import org.meveo.model.crm.custom.CustomFieldValues;
import org.apache.commons.lang3.SerializationUtils;

public class CheckOVHToken extends Script {

    private static final Logger log = LoggerFactory.getLogger(ListOVHServersScript.class);

    private CrossStorageApi crossStorageApi = getCDIBean(CrossStorageApi.class);

    private RepositoryService repositoryService = getCDIBean(RepositoryService.class);

    private Repository defaultRepo = repositoryService.findDefaultRepository();

    private CustomEntityTemplateService customEntityTemplateService = getCDIBean(CustomEntityTemplateService.class);

    private CustomFieldTemplateService customFieldTemplateService = getCDIBean(CustomFieldTemplateService.class);

    @Override
    public void execute(Map<String, Object> parameters) throws BusinessException {
        super.execute(parameters);
    }

    public void checkOVHToken(Credential credential, ServiceProvider openstack) {
        log.info("call CheckOVHToken");
        // Verification of the token
        OffsetDateTime currentDate = OffsetDateTime.now();
        OffsetDateTime expireDate = OffsetDateTime.parse(credential.getTokenExpiry().toString());
        if (currentDate.isAfter(expireDate)) {
            try {
                // Dechiffrement du mot de passe
                String stringToDecrypt = credential.getPasswordSecret();
                String codeClass = credential.getClass().getSimpleName();
                CustomEntityTemplate cet = customEntityTemplateService.findByCode(codeClass);
                List<Object> objectsToHash = new ArrayList<>();
              	CustomEntityInstance credentialCEI = CEIUtils.pojoToCei(credential);
                Map<String, CustomFieldTemplate> customFieldTemplates = customFieldTemplateService.findByAppliesTo(cet.getAppliesTo());
                var hash = CEIUtils.getHash(credentialCEI, customFieldTemplates);
                String stringDecrypted = PasswordUtils.decryptNoSecret(hash, stringToDecrypt);
                // Creation du body
                HashMap<String, Object> master = new HashMap<String, Object>();
                HashMap<String, Object> auth = new HashMap<String, Object>();
                HashMap<String, Object> identity = new HashMap<String, Object>();
                HashMap<String, Object> password = new HashMap<String, Object>();
                HashMap<String, Object> user = new HashMap<String, Object>();
                HashMap<String, Object> domain = new HashMap<String, Object>();
                ArrayList<String> method = new ArrayList<String>();
                method.add("password");
                domain.put("id", "default");
                user.put("password", stringDecrypted);
                user.put("domain", domain);
                user.put("name", credential.getUsername());
                password.put("user", user);
                identity.put("methods", method);
                identity.put("password", password);
                auth.put("identity", identity);
                master.put("auth", auth);
                String resp = JacksonUtil.toStringPrettyPrinted(master);
                // Creation of the identity token
                Client client = ClientBuilder.newClient();
                WebTarget target = client.target("https://auth." + openstack.getApiBaseUrl() + "/v3/auth/tokens");
                Response response = target.request().post(Entity.json(resp));
                credential.setToken(response.getHeaderString("X-Subject-Token"));
                credential.setTokenExpiry(currentDate.plusDays(1).toInstant());
                try {
                    crossStorageApi.createOrUpdate(defaultRepo, credential);
                } catch (Exception ex) {
                    log.error("error update credentials {} :{}", credential.getUuid(), ex.getMessage());
                }
                response.close();
            } catch (Exception ex) {
                log.error(ex.getMessage());
            }
        }
    }
}
